parser.custom-parsing:

					/* Dates */
					fields.put("newTrnDate", fields.getActualYear().substring(2) + fields.getByPosition(13));
					fields.put("newCntDate", fields.getActualYear().substring(2) + fields.getByPosition(13));

					/*Date : mmddyyyy hh:mm:ss*/
					fields.put("dateTime", fields.getActualYear().substring(2) + fields.getByPosition(7).substring(0,4) + " " 
					+ fields.getByPosition(7).substring(4,6) + ":" + fields.getByPosition(7).substring(6,8) + ":" 
					+ fields.getByPosition(7).substring(8));

					/* Primary Account Number - Expiration Date*/
					if (fields.getByPosition(22) != null) 
						if("01".equals(fields.getByPosition(22).substring(0,2))) 
						{
							/* Primary Account Number -> field 2 data was entered Manually */
							fields.put("card", fields.getByPosition(2));

							/* Expiration Date of the Card */	
							fields.put("cardExpirationDate", fields.getByPosition(14).substring(17, 21));
						}
						else
						{
							/* Primary Account Number Track II -> field 35 */
							fields.put("card", fields.getByPosition(35).substring(0,16));

							/* Expiration Date of the Card */	
							fields.put("cardExpirationDate", fields.getByPosition(35).substring(17, 21));						
						}
					else
					{
						/* Primary Account Number Track II -> field 35 */
						fields.put("card", fields.getByPosition(35).substring(0,16));

						/* Expiration Date of the Card */	
						fields.put("cardExpirationDate", fields.getByPosition(35).substring(17, 21));						
					}

					String codTran2 = fields.getByPosition(3).substring(2,4);
					String codTran3 = fields.getByPosition(3).substring(4,6);

					String codTran2 = fields.getByPosition(3).substring(2,4);
					String codTran3 = fields.getByPosition(3).substring(4,6);

					/* Product Type */
					if("20".equals(codTran2))
						fields.put("debProduct","3");
					else if("10".equals(codTran2))
						fields.put("debProduct","4");

					if("20".equals(codTran3))
						fields.put("creProduct","3");
					else if("10".equals(codTran3))
						fields.put("creProduct","4");

					/* Reference Number */
					fields.put("refNumber",fields.getByPosition(37));

					/* Country */
					fields.put("country",fields.getByPosition(43).substring(fields.getByPosition(43).length() - 2));

                    /* Other Network */
                    fields.put("otherNetwork", "N");

					/* Terminal Type */
					if("02".equals(fields.get("transactionTypeHeader")))
					{
						fields.put("terminalType","POS");
						/* Origin : POS */
						if (fields.getByPosition(43) != null) 
						{
							if("CR".equals(fields.getByPosition(43).substring(38, 40)))
								fields.put("origin", "N");
							else
								fields.put("origin", "I");
						} 
					}
					else
					{
						fields.put("terminalType","ATM");
						/* Origin : ATM */
						if (fields.getByPosition(48) != null) 						
						{
							if("188".equals(fields.getByPosition(48).substring(30, 33)))
							{
								fields.put("origin", "N");
								if (fields.getByPosition(60) != null) 
									if("CR".equals(fields.getByPosition(60).substring(0, 2)))
										fields.put("otherNetwork", "N");
									else
										fields.put("otherNetwork", "S");
							}
							else
								fields.put("origin", "I");
						} 
					}

					/* Replacement Amounts */
					if (fields.getByPosition(95) != null) 
					{
						System.out.println("### Reverso Parcial ###" + fields.getByPosition(95).substring(0, 10) + "." + fields.getByPosition(95).substring(10, 12));
						fields.put("replaceAmount", fields.getByPosition(95).substring(0, 10) + "." + fields.getByPosition(95).substring(10, 12));
					}

					/* Conversion Rate */
					fields.put("convRate",fields.getByPosition(10));

					/* Original Transaction Currency */
					fields.put("origTrnCurrency",fields.getByPosition(51));

					/* Return value - duplicate Trn */
					fields.put("returnDuplicateTrn","N");	

					/* Reversed Value */
					if("0420".equals(fields.get("messageType")))
						fields.put("newCorr","S");
					else
						fields.put("newCorr","N");

					/* Terminal */
					fields.put("newTerminal",fields.getByPosition(41).trim());

					/* Location */
					fields.put("newLocation",fields.getByPosition(43).trim());

					/* Keys */
					fields.put("cmpKey","ATH"+fields.get("newTrnDate")+fields.getByPosition(35).substring(0, 16)+fields.get("newTerminal")+fields.getByPosition(11).substring(2)+"0"+fields.getByPosition(4));					
					fields.put("trcKey","ATH"+fields.get("newTrnDate")+fields.get("newTerminal")+fields.getByPosition(11)+fields.get("card")+"00"+fields.getByPosition(4));					
					fields.put("corrAut","N");

					if(fields.getByPosition(90) != null) {
						fields.put("trnKey","ATH"+fields.get("newTrnDate")+fields.get("newTerminal")+fields.getByPosition(90).substring(4, 16).trim());	
					}else
						fields.put("trnKey","ATH"+fields.get("newTrnDate")+fields.get("newTerminal")+fields.getByPosition(37));
					fields.put("trnData", fields.get("messageType") + fields.getByPosition(3) + "_/" +
						fields.get("newTerminal") + "/" + fields.get("card") + fields.getByPosition(11) +
						"_/00000000000+");


					String newP44 = "4" + fields.fillParamsBefore(fields.formatMoneyValueToString(fields.get("T1001-o_ledgBalanceDebit")),12,'0') + fields.fillParamsBefore(fields.formatMoneyValueToString(fields.get("T1000-o_availableBalanceDebit")), 12,'0');
					fields.putByPosition(44, newP44);
					fields.putByPosition(17, fields.getActualMonth() + fields.getActualDay());
					if("0200".equals(fields.get("messageType")))
						fields.put("messageType", "0210");
					else if("0800".equals(fields.get("messageType")))
						fields.put("messageType", "0810");	
					else if("0420".equals(fields.get("messageType")))
						fields.put("messageType", "0430");	
					else if("0220".equals(fields.get("messageType")))
						fields.put("messageType", "0230");	
					/**
                     for not supported or Error Parsing 12
                     **/
					 if(Boolean.parseBoolean(fields.get("$notsupported")) || Boolean.parseBoolean(fields.get("$errorParsing"))){
					    fields.put("P39-responseCode", "12");
						fields.put("S102-accountIdentification1", null);
						fields.put("S103-accountIdentification2", null);
					}						
					/**
                     for timeout 19
                     **/
                     if(Boolean.parseBoolean(fields.get("$timeout"))==true) {
                        fields.put("P39-responseCode", "19");
                    }
